DROP TABLE IF EXISTS `banner`;
CREATE TABLE `banner` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键id;',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间;',
  `update_time` datetime DEFAULT '1970-01-01 00:00:00' COMMENT '更新时间;',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '删除;',
  `enabled` tinyint(1) NOT NULL DEFAULT '1' COMMENT '启用;',
  `sort` int NOT NULL DEFAULT '100' COMMENT '排序;',
  `remark` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '描述;',
  `type` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '类型;',
  `lang` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'en-US' COMMENT '语言;',
  `title` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '标题;',
  `cname` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '中文名称;',
  `intro` varchar(1000) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '简介;',
  `pic` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图片;',
  `pic_select` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图片2;选中图/封面图',
  `icon` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '字体图标名称;',
  `url` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '链接地址;',
