-- ----------------------------
-- Table structure for execute_order_step_command
-- ----------------------------
DROP TABLE IF EXISTS `execute_order_step_command`;
CREATE TABLE `execute_order_step_command` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键id;',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间;',
  `update_time` datetime DEFAULT '1970-01-01 00:00:00' COMMENT '更新时间;',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '删除;',
  `no` int NOT NULL DEFAULT '0' COMMENT '命令序号;',
  `order_id` bigint DEFAULT NULL COMMENT '订单id;',
  `step_id` bigint DEFAULT NULL COMMENT '步骤id;',
  `step_name` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '步骤名称;',
  `step_sort` int NOT NULL DEFAULT '0' COMMENT '步骤排序;',
  `step_no` decimal(20,6) NOT NULL DEFAULT '0.000000' COMMENT '步骤序号1到n;',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '解析前执行内容;',
  `content_param_required` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '需要参数;',
  `content_param_json` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '解析参数;',
  `content_after` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '解析后执行内容;',
  `success` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否成功;',
  `remark` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '备注;',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=214 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='执行订单的步骤命令行';

