INSERT INTO `execute_order_step_command` (`id`, `create_time`, `update_time`, `deleted`, `no`, `order_id`, `step_id`, `step_name`, `step_sort`, `step_no`, `content`, `content_param_required`, `content_param_json`, `content_after`, `success`, `remark`) VALUES (65, '2025-03-29 09:15:21', '1970-01-01 00:00:00', 0, 1, 16, 3, '连接服务器-重启-ssh-jar', 10040, 3.000000, '<#list servers as server>\n  <#list modules as module>\n    <#if module.code != \"libs\">\n      # 拷贝main-jar包\n      scp -i ${server.privateKeyLocalPath} ${platform.deployRoot}-services/${module.code}-service/target/${module.code}-service.jar ${server.username}@${server.host}:/project/${platform.code}/new/${module.code}-servce.jar\n\n      <remote server=\"${json(server)}\">\n        # 结束旧进程\n        pid=$(ps -ef | grep \"/project/${platform.code}/${module.code}-servce.jar\" | grep -v grep | awk \'{print $2}\')\n        if [ -n \"$pid\" ]; then\n        kill -9 $pid\n        echo \"Previous process stopped.\"\n        fi\n\n        # 备份旧的 JAR 文件和日志文件\n        BACKUP_DIR=\"/project/${platform.code}/backup/$(date +%Y%m%d%H%M%S)\"\n        mkdir -p \"$BACKUP_DIR\"\n        cp /project/${platform.code}/${module.code}-servce.jar \"$BACKUP_DIR/${module.code}-servce.jar\"\n        cp /project/${platform.code}/${module.code}.log \"$BACKUP_DIR/${module.code}.log\"\n        echo \"Backup completed to $BACKUP_DIR.\"\n\n        # 启动新进程\n        mkdir -p /project/${platform.code}/new\n        cp -f /project/${platform.code}/new/${module.code}-servce.jar /project/${platform.code}/${module.code}-servce.jar\n        export LC_ALL=en_US.UTF-8\n        cd /project/${platform.code}/\n        nohup java ${module.javaEnv} -Dloader.path=/project/${platform.code}/lib -jar -Dfile.encoding=UTF-8 -Dspring.profiles.active=test,platform /project/${platform.code}/${module.code}-servce.jar > /project/${platform.code}/${module.code}.log 2>&1 & disown\n        sleep 3\n\n        # 检查启动成功\n        tail -f /project/${platform.code}/${module.code}.log | while read line; do\n        echo \"$line\"\n        if [[ \"$line\" == *Started* ]]; then\n        echo \"Application started successfully!\"\n        break\n        fi\n        done\n      </remote>\n    </#if>\n  </#list>\n</#list>', 'servers,modules,platform', '{\"servers\":[{\"code\":\"120.26.16.116\",\"name\":\"爆蚁erp-test\",\"host\":\"120.26.16.116\",\"port\":\"22\",\"privateKeyLocalPath\":\"/root/.ssh/id_rsa_baoyi\",\"username\":\"root\",\"privateKeyPassword\":\"\"}],\"repositories\":[\"git@gitee.com:lzb-framework/framework-cloud.git\",\"git@gitee.com:lzb-parent/parent-cloud.git\",\"git@gitee.com:baoyi_1/platform.git\"],\"modules\":[{\"code\":\"admin\",\"name\":\"admin\",\"javaEnv\":\"-Xms128m -Xmx1024m\"}],\"platform\":\"baoyi\"}', '      # 拷贝main-jar包\n      scp -i /root/.ssh/id_rsa_baoyi /project/snowball/file/snowball_workspace/16/platform/baoyi-services/admin-service/target/admin-service.jar root@120.26.16.116:/project/baoyi/new/admin-servce.jar\n\n      <remote server=\"{\"privateKeyLocalPath\":\"/root/.ssh/id_rsa_baoyi\",\"code\":\"120.26.16.116\",\"privateKeyPassword\":\"\",\"port\":\"22\",\"name\":\"爆蚁erp-test\",\"host\":\"120.26.16.116\",\"username\":\"root\"}\">\n        # 结束旧进程\n        pid=$(ps -ef | grep \"/project/baoyi/admin-servce.jar\" | grep -v grep | awk \'{print $2}\')\n        if [ -n \"$pid\" ]; then\n        kill -9 $pid\n        echo \"Previous process stopped.\"\n        fi\n\n        # 备份旧的 JAR 文件和日志文件\n        BACKUP_DIR=\"/project/baoyi/backup/$(date +%Y%m%d%H%M%S)\"\n        mkdir -p \"$BACKUP_DIR\"\n        cp /project/baoyi/admin-servce.jar \"$BACKUP_DIR/admin-servce.jar\"\n        cp /project/baoyi/admin.log \"$BACKUP_DIR/admin.log\"\n        echo \"Backup completed to $BACKUP_DIR.\"\n\n        # 启动新进程\n        mkdir -p /project/baoyi/new\n        cp -f /project/baoyi/new/admin-servce.jar /project/baoyi/admin-servce.jar\n        export LC_ALL=en_US.UTF-8\n        cd /project/baoyi/\n        nohup java -Xms128m -Xmx1024m -Dloader.path=/project/baoyi/lib -jar -Dfile.encoding=UTF-8 -Dspring.profiles.active=test,platform /project/baoyi/admin-servce.jar > /project/baoyi/admin.log 2>&1 & disown\n        sleep 3\n\n        # 检查启动成功\n        tail -f /project/baoyi/admin.log | while read line; do\n        echo \"$line\"\n        if [[ \"$line\" == *Started* ]]; then\n        echo \"Application started successfully!\"\n        break\n        fi\n        done\n      </remote>\n', 0, '');
