INSERT INTO `execute_order_step_command` (`id`, `create_time`, `update_time`, `deleted`, `no`, `order_id`, `step_id`, `step_name`, `step_sort`, `step_no`, `content`, `content_param_required`, `content_param_json`, `content_after`, `success`, `remark`) VALUES (191, '2025-04-26 22:12:24', '1970-01-01 00:00:00', 0, 1, 43, 3, '连接服务器-重启-ssh-jar', 10040, 3.000000, '<#list servers as server>\n  <#list modules as module>\n    <#if module.code != \"libs\">\n      # 拷贝main-jar包\n      scp -i ${server.privateKeyLocalPath} @/platform/${platform.code}-services/${platform.code}-${module.code}-service/target/${platform.code}-${module.code}-service.jar ${server.username}@${server.host}:${platform.deployRoot}/new/${platform.code}-${module.code}-service.jar\n\n      <remote server=\"${json(server)}\">\n        # 结束旧进程\n        pid=$(ps -ef | grep \"${platform.deployRoot}/${platform.code}-${module.code}-service.jar\" | grep -v grep | awk \'{print $2}\')\n        if [ -n \"$pid\" ]; then\n        kill -9 $pid\n        echo \"Previous process stopped.\"\n        fi\n\n        # 备份旧的 JAR 文件和日志文件\n        BACKUP_DIR=\"${platform.deployRoot}/backup/$(date +%Y%m%d%H%M%S)\"\n        mkdir -p \"$BACKUP_DIR\"\n        cp ${platform.deployRoot}/${platform.code}-${module.code}-service.jar \"$BACKUP_DIR/${platform.code}-${module.code}-service.jar\"\n        cp ${platform.deployRoot}/${module.code}.log \"$BACKUP_DIR/${module.code}.log\"\n        echo \"Backup completed to $BACKUP_DIR.\"\n\n        # 启动新进程\n        mkdir -p ${platform.deployRoot}/new\n        cp -f ${platform.deployRoot}/new/${platform.code}-${module.code}-service.jar ${platform.deployRoot}/${platform.code}-${module.code}-service.jar\n        export LC_ALL=en_US.UTF-8\n        cd ${platform.deployRoot}/\n        nohup java ${module.javaEnv} -Dloader.path=${platform.deployRoot}/lib -jar -Dfile.encoding=UTF-8 ${platform.deployRoot}/${platform.code}-${module.code}-service.jar > ${platform.deployRoot}/${module.code}.log 2>&1 & disown\n        sleep 3\n\n        # 检查启动成功\n        tail -f ${platform.deployRoot}/${module.code}.log | while read line; do\n        echo \"$line\"\n        if [[ \"$line\" == *Started* ]]; then\n        echo \"Application started successfully!\"\n        break\n        fi\n        done\n      </remote>\n    </#if>\n  </#list>\n</#list>', 'servers,modules,platform', '{\"servers\":[{\"code\":\"113.45.237.207\",\"name\":\"baoyi_prod\",\"host\":\"113.45.237.207\",\"port\":\"22\",\"privateKeyLocalPath\":\"/Users/zubin/fsdownload/id_rsa_baoyi_prod\",\"username\":\"root\",\"privateKeyPassword\":\"\"}],\"repositories\":[\"git@gitee.com:lzb-framework/framework-cloud.git\",\"git@gitee.com:lzb-parent/parent-cloud.git\",\"git@gitee.com:baoyi_1/platform.git\"],\"modules\":[{\"code\":\"admin\",\"name\":\"admin\",\"javaEnv\":\"-Xms64m -Xmx128m -Xshare:auto\"}],\"platform\":{\"code\":\"baoyi\",\"deployRoot\":\"/data/project/baoyi\"}}', '      # 拷贝main-jar包\n      scp -i /Users/zubin/fsdownload/id_rsa_baoyi_prod /Users/Shared/file/snowball_workspace/43/platform/baoyi-services/baoyi-admin-service/target/baoyi-admin-service.jar root@113.45.237.207:/data/project/baoyi/new/baoyi-admin-service.jar\n\n      <remote server=\"{\"privateKeyLocalPath\":\"/Users/zubin/fsdownload/id_rsa_baoyi_prod\",\"code\":\"113.45.237.207\",\"privateKeyPassword\":\"\",\"port\":\"22\",\"name\":\"baoyi_prod\",\"host\":\"113.45.237.207\",\"username\":\"root\"}\">\n        # 结束旧进程\n        pid=$(ps -ef | grep \"/data/project/baoyi/baoyi-admin-service.jar\" | grep -v grep | awk \'{print $2}\')\n        if [ -n \"$pid\" ]; then\n        kill -9 $pid\n        echo \"Previous process stopped.\"\n        fi\n\n        # 备份旧的 JAR 文件和日志文件\n        BACKUP_DIR=\"/data/project/baoyi/backup/$(date +%Y%m%d%H%M%S)\"\n        mkdir -p \"$BACKUP_DIR\"\n        cp /data/project/baoyi/baoyi-admin-service.jar \"$BACKUP_DIR/baoyi-admin-service.jar\"\n        cp /data/project/baoyi/admin.log \"$BACKUP_DIR/admin.log\"\n        echo \"Backup completed to $BACKUP_DIR.\"\n\n        # 启动新进程\n        mkdir -p /data/project/baoyi/new\n        cp -f /data/project/baoyi/new/baoyi-admin-service.jar /data/project/baoyi/baoyi-admin-service.jar\n        export LC_ALL=en_US.UTF-8\n        cd /data/project/baoyi/\n        nohup java -Xms64m -Xmx128m -Xshare:auto -Dloader.path=/data/project/baoyi/lib -jar -Dfile.encoding=UTF-8 /data/project/baoyi/baoyi-admin-service.jar > /data/project/baoyi/admin.log 2>&1 & disown\n        sleep 3\n\n        # 检查启动成功\n        tail -f /data/project/baoyi/admin.log | while read line; do\n        echo \"$line\"\n        if [[ \"$line\" == *Started* ]]; then\n        echo \"Application started successfully!\"\n        break\n        fi\n        done\n      </remote>\n', 0, '');
