-- ----------------------------
-- Table structure for execute_param
-- ----------------------------
DROP TABLE IF EXISTS `execute_param`;
CREATE TABLE `execute_param` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键id;',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间;',
  `update_time` datetime DEFAULT '1970-01-01 00:00:00' COMMENT '更新时间;',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '删除;',
  `group_id` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT '' COMMENT '我的我的分组;',
  `my_template_id` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT '' COMMENT '我的模板;',
  `model_code` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '参数模型;',
  `value` varchar(2048) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '值;',
  `name` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '名称;',
  `remark` varchar(200) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '备注;',
  `sort` int NOT NULL DEFAULT '100' COMMENT '排序;',
  `enabled` tinyint(1) NOT NULL DEFAULT '1' COMMENT '开关;',
  `default_value` varchar(2048) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT '' COMMENT '默认值;取字符串 或者 值选项的编号,多个值换行符分隔',
  `execute_edit_type` varchar(32) COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'CAN_EDIT' COMMENT '是否要编辑;#Enum#EnumEditType  CAN_EDIT-可以编辑 MUST_EDIT-必须编辑 CANNOT_EDIT-不能编辑',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `uk_modelCode_groupId_myTemplateId` (`model_code`,`group_id`,`my_template_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='参数可选值';

