INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (2, '2025-03-27 10:59:51', '1970-01-01 00:00:00', 0, '编译打包-maven', 2, 'cd @/parent-cloud\nmvn clean install -DskipTests=true || exit 1 \n', '', 100, 1);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (3, '2025-03-27 10:59:51', '1970-01-01 00:00:00', 0, '连接服务器-复制新软件包ssh-jar', 5, '<#list servers as server>\n    <#list modules as module>\n        <#if module.code != \"libs\">\n            scp -i ${server.privateKeyLocalPath} @/platform/${platform}-services/${platform}-${module.code}-service/target/${module.code}-service.jar ${server.username}@${server.host}:/project/${platform}/new/${platform}-${module.code}.jar\n        </#if>\n    </#list>\n</#list>', '', 100, 0);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (4, '2025-03-27 10:59:51', '1970-01-01 00:00:00', 0, '连接服务器-重启-ssh-jar', 3, '<#list servers as server>\n  <#list modules as module>\n    <#if module.code != \"libs\">\n      # 拷贝main-jar包\n      scp -i ${server.privateKeyLocalPath} @/platform/${platform}-services/${platform}-${module.code}-service/target/${platform}-${module.code}-service.jar ${server.username}@${server.host}:/project/${platform}/new/${platform}-${module.code}-service.jar\n\n      <remote server=\"${json(server)}\">\n        # 结束旧进程\n        pid=$(ps -ef | grep \"/project/${platform}/${platform}-${module.code}-service.jar\" | grep -v grep | awk \'{print $2}\')\n        if [ -n \"$pid\" ]; then\n        kill -9 $pid\n        echo \"Previous process stopped.\"\n        fi\n\n        # 备份旧的 JAR 文件和日志文件\n        BACKUP_DIR=\"/project/${platform}/backup/$(date +%Y%m%d%H%M%S)\"\n        mkdir -p \"$BACKUP_DIR\"\n        cp /project/${platform}/${platform}-${module.code}-service.jar \"$BACKUP_DIR/${platform}-${module.code}-service.jar\"\n        cp /project/${platform}/${module.code}.log \"$BACKUP_DIR/${module.code}.log\"\n        echo \"Backup completed to $BACKUP_DIR.\"\n\n        # 启动新进程\n        mkdir -p /project/${platform}/new\n        cp -f /project/${platform}/new/${platform}-${module.code}-service.jar /project/${platform}/${platform}-${module.code}-service.jar\n        export LC_ALL=en_US.UTF-8\n        cd /project/${platform}/\n        nohup java ${module.javaEnv} -Dloader.path=/project/${platform}/lib -jar -Dfile.encoding=UTF-8 /project/${platform}/${platform}-${module.code}-service.jar > /project/${platform}/${module.code}.log 2>&1 & disown\n        sleep 3\n\n        # 检查启动成功\n        tail -f /project/${platform}/${module.code}.log | while read line; do\n        echo \"$line\"\n        if [[ \"$line\" == *Started* ]]; then\n        echo \"Application started successfully!\"\n        break\n        fi\n        done\n      </remote>\n    </#if>\n  </#list>\n</#list>', '', 100, 1);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (5, '2025-03-27 10:59:51', '1970-01-01 00:00:00', 0, '压缩lib-复制lib', 6, '<#list servers as server>\n    <#list modules as module>\n        <#if module.code == \"libs\">\n            cd @/platform/${platform}-all-include/target/lib\n            tar --no-xattr -czf ../libs.tar.gz *.jar\n        </#if>\n    </#list>\n</#list>', '', 100, 1);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (6, '2025-03-27 10:59:51', '1970-01-01 00:00:00', 0, '连接服务器-解压', 6, '<#list servers as server>\n    <#list modules as module>\n        <#if module.code == \"libs\">\n            scp -i ${server.privateKeyLocalPath} @/platform/${platform}-all-include/target/libs.tar.gz ${server.username}@${server.host}:/project/${platform}/\n            <remote server=\"${json(server)}\">\n                cd /project/${platform}/\n                tar -xzf libs.tar.gz -C lib\n            </remote>\n        </#if>\n    </#list>\n</#list>', '', 100, 1);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (8, '2025-03-27 10:59:52', '1970-01-01 00:00:00', 0, '拉取ui代码-git-yarnInstall', 9, '# 拉取ui代码 并install\n<#list repositories as repository>\n  <#list servers as server>\n    <#list modules as module>\n        <#if (\'ui-\'+module.code) == getGitName(repository)>\n            cd @/\n            rm -rf ${getGitName(repository)}\n            git clone ${repository}\n            cd ${getGitName(repository)}\n            cd src\n            <#list repositories_parent_ui as repository_parent_ui>\n                rm -rf ${getGitName(repository_parent_ui)}\n                git clone ${repository_parent_ui}\n            </#list>\n            cd ..\n            yarn install --silent || exit 1 \n            npm run \'build prod\' || exit 1 \n        </#if>\n    </#list>\n  </#list>\n</#list>\n', '', 100, 1);
INSERT INTO `execute_step_command_copy2` (`id`, `create_time`, `update_time`, `deleted`, `name`, `step_id`, `content`, `remark`, `sort`, `enabled`) VALUES (9, '2025-03-27 10:59:52', '1970-01-01 00:00:00', 0, '部署ui代码', 11, '#压缩 复制到服务器 备份 解压(完成部署)\n<#list servers as server>\n  <#list modules as module>\n  cd @/ui-${module.code}/dist\n  tar --no-xattr -czf ../ui-${module.code}.tar.gz ./*\n  scp -i ${server.privateKeyLocalPath} @/ui-${module.code}/ui-${module.code}.tar.gz ${server.username}@${server.host}:/project/${platform}/new/\n  <remote server=\"${json(server)}\">\n      BACKUP_DIR=\"/project/${platform}/backup/$(date +%Y%m%d%H%M%S)\"\n      mkdir -p \"$BACKUP_DIR\"\n      cp /project/${platform}/new/ui-${module.code}.tar.gz \"$BACKUP_DIR/\"\n      echo \"Backup completed to $BACKUP_DIR.\"\n\n      cd /project/${platform}/new\n      tar -xzf ui-${module.code}.tar.gz -C /project/${platform}/ui${module.code}\n  </remote>\n  </#list>\n</#list>', '', 100, 1);
COMMIT;

